import React from 'react';
import logo from './resources/nature.jpg';
import iphone from './resources/sport.jpg';
import arrowLeft from './resources/arrow-left-circle.svg';
import arrowRight from './resources/arrow-right-circle.svg';
import data from './data/products.json';
import './App.css';
import { useLocation } from 'react-router-dom';


export default class ProductPage extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            photos: [iphone,logo],
            indexPhoto: 0
        }
    }

    onclick(type){
        this.setState(prevState => {
           return {
               indexPhoto: type === 'next' ? (prevState.indexPhoto < prevState.photos.length-1 ? prevState.indexPhoto + 1: prevState.indexPhoto) : (prevState.indexPhoto !== 0 ? prevState.indexPhoto - 1: 0)}
        });
    }

    changeImg(img){
        this.setState({
            indexPhoto: this.state.photos.indexOf(img)
        })
    }

    render(){
        const location = window.location.pathname.split('/')[2] 
        const url = data.filter( element => element.id === location)[0];
        
        return(
            <div style={{marginTop: 100}}>
                <p >{url.location}</p>
                <p >{url.location}</p>
                <p >{url.location}</p>
            </div>
        )
    }
}
