import { login, logout } from "../api/Api";

export const signInMiddleware = store => next => action => {
    if(action.action === "SIGN_IN"){
        login(action.payload)
            .then(response => {
                if(response.data.message === "user logged in"){
                    next(action);
                } else {
                    alert("Could not login!")
                }
            })
            .catch(err => {
                console.log(err);
            })
    }    
}

export const signOutMiddleware = store => next => action => {
    if(action.payload === "SIGN_OUT"){
        logout()
            .then(response => {
                console.log(response);
                next(action)
            })
            .catch(err => {
                console.log(err);
            })
    }    
}
