import axios from "axios";
import { useEffect, useState } from "react";

function UploadImage(){

    const createImage = (url) =>
        new Promise((resolve, reject) => {
            const image = new Image();
            image.addEventListener("load", () => resolve(image));
            image.addEventListener("error", (error) => reject(error));
            image.setAttribute("crossOrigin", "anonymous"); // needed to avoid cross-origin issues on CodeSandbox
            image.src = url;
        });

    const [doc, setDoc] = useState();
    const [image, setImage] = useState(null);


    const upload = (e) => {
        const myImage =  createImage(image);
        console.log(myImage.width)
    }

    const getImage = () => {
        axios.get("http://localhost:3080/file?type=img&id=61a45dee1d898f49a3b1f2da")
            .then()
    }

    const onFileChange = event => {
        if(event.target.files && event.target.files.length > 0){
            const reader = new FileReader();
            reader.readAsDataURL(event.target.files[0]);
            reader.addEventListener('load', () => {
                console.log(reader.result);
                setImage(reader.result);
            })
        }
    };


    return(
        <div>
            <div className="upload">
                <input className="form-control" accept="image/*" type="file" id="file" name="file"  onChange={onFileChange} style={{marginBottom: '10px'}}></input>
                <button type="button" className="btn btn-primary" onClick={upload}>Upload</button>
            </div>
            <hr/>
            <div className="show-images">
                <div>
                    <img src="" width="250" height="250" alt="pic"/>
                </div>
                <button type="button" className="btn btn-primary mt-3" onClick={getImage}>Show image</button>
            </div>
        </div>
    )
}

export default UploadImage;

