import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./ProductCard.css";
import "./NewProductCard.css"

function ProductCard(props) {
  if (props.annonce) {
    props = props.annonce;
  }

  const [images, title, location, price, id] = [
    props.img,
    props.name || "no name",
    props.location || "Kristiansand",
    props.price || "0",
    props.id || null,
  ];

  const [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

  return(
    <div className="productCard">
        <img src={
              (images.length > 0) && (images !== undefined) && (images !== null)
                ? images[imgIndex].location
                : "https://static.toiimg.com/photo/msid-58515713,width-96,height-65.cms"
            } alt="Denim Jeans" style={{width: '100%'}}/>
        <h3>{title}</h3>
        <p className="price">{price}</p>
        <p>{location}</p>
        <p>Some text about the nature right here.</p>
        <button
            className="btn btn-primary"
            data-toggle="tooltip"
            title="Send Message"
            onClick={(e) => e.preventDefault()}
          >
            Send Melding <i className="fas fa-envelope"></i>
          </button>
          <button
            type="button"
            className="btn btn-outline-danger"
            data-toggle="tooltip"
            title="Add to Favorites"
            onClick={(e) => e.preventDefault()}
          >
            <i className="fas fa-heart"></i>
          </button>
          <button className="btn btn-secondary"><i class="fas fa-link"></i></button>
</div>
  )
}

export default ProductCard;


