import axios from 'axios';

const config = require('../config/cfg.js');

var isLoggedIn = false;
var smplMsg = 'hello world'

const instanceAxs = axios.create({
    baseURL: config.serverApi,
    withCredentials: true
});

const checkLogin = async () => {
    instanceAxs.get('/checklogin')
        .then(result => {
            let response = result.data.message;
            console.log(response);
            if (response === "user logged in"){
                isLoggedIn = true;
            } else {
                isLoggedIn = false;
            }
            console.log(isLoggedIn);
            return result.data.message;
        })
}

const setLogin = async () => {
    var res = checkLogin(); 
    res.then(function(result) {
        console.log(result)
        if (result === "user logged in"){
            isLoggedIn = true;
        } else {
            isLoggedIn = false;
        }
        console.log(isLoggedIn);
    })
}

export {smplMsg, checkLogin, setLogin};
