import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import './style.css';

function Product (props){
  const [images,title,location,price,desc] = [props.img, props.name,props.location,props.price,props.desc];

  var [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

      return(
        <Link to="login"><div className="card h-100 border-2 shadow" >
              <div className="card-img" 
                  onMouseEnter={() => setHovered(true)}
                  onMouseLeave={() => setHovered(false)}
                >                      
                <img src={images[imgIndex]}  className="img-responsive w-100" alt="alt-image-cap" id="product-photo"></img>
                  {isHovered == true && 
                  <div className="d-flex card-img-overlay justify-content-between" id="arrows">
                      <button className="btn btn-light rounded-circle" id="left-arrow" onClick={(e) => { e.preventDefault(); imgIndex != 0 && setImgIndex(imgIndex-1)}}><i className="fas fa-arrow-left"></i></button>
                      <button className="btn btn-light rounded-circle " id="right-arrow" onClick={(e) =>{ e.preventDefault(); imgIndex < images.length-1 && setImgIndex(imgIndex+1)}}><i className="fas fa-arrow-right"></i></button>
                  </div>}
              </div>                  

            
            <div className="card-body d-flex flex-column">
              <div className="d-flex mb-2">
                <h5 className="card-title" id="pris" onClick={((e) => e.preventDefault())}>{price + " kr"}</h5>
                <p className="card-text " id="pris">&middot;</p>
                <p className="card-text " id="pris">{location}</p>
                <div className="ms-auto">
                  <Link to="/menu"><button className="btn btn-primary me-2" data-toggle="tooltip" title="Send Message"><i className="fas fa-envelope"></i></button></Link>
                  <button type="button" className="btn btn-outline-danger" data-toggle="tooltip" title="Add to Favorites"><i className="fas fa-heart"></i></button>
                </div>
              </div>
                
              <p className="card-text me-2" style={{pointerEvents: 'none'},{position: 'relative'}}>{title}</p>              
            </div>
          </div>
          </Link>
      )   
}

export default Product;
