import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./ProductCard.css";
import "./NewProductCard.css"

function ProductCard(props) {
  if (props.annonce) {
    props = props.annonce;
  }

  const [images, title, location, price, id] = [
    props.img,
    props.name || "no name",
    props.location || "Kristiansand",
    props.price || "0",
    props.id || null,
  ];

  const [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

  return(
    <div className="card">
        <img src="https://static.toiimg.com/photo/msid-58515713,width-96,height-65.cms" alt="Denim Jeans" style="width:100%"/>
        <h1>Natural Nature</h1>
        <p class="price">$199.99</p>
        <p>Some text about the nature. Super slim and comfy lorem ipsum lorem jeansum. Lorem jeamsun denim lorem jeansum.</p>
        <p><button>Add to Cart</button></p>
</div>
  )
}

export default ProductCard;


