import axios from 'axios';

const config = require('../config/cfg.js');

var isLoggedIn = false;

const instanceAxs = axios.create({
    baseURL: config.serverApi,
    withCredentials: true
});

const checkLogin = () => {
    instanceAxs.get('/checklogin')
        .then(result => {
            return result.data.message;
        })
}

const setLoggedIn = () => {
    if (checkLogin === "user logged in"){
        isLoggedIn = true;
        console.log(isLoggedIn)
    } else {
        isLoggedIn = false;
    }
}

module.exports = {
    isLoggedIn, checkLogin, setLoggedIn
}
