import React, { useState } from 'react';
import {BrowserRouter as Router,Route,Link,useRouteMatch } from 'react-router-dom'
import ProductPage from './'
import './style.css';

function Product (props){
  const [images,title,location,price,id] = [props.img, props.name,props.location,props.price,props.id];
  let { path, url } = useRouteMatch();

  var [isHovered, setHovered] = useState(false);
  const [imgIndex, setImgIndex] = useState(0);

  const data = props.data;

      return(
        <div className="card h-100 border-2 shadow" >
              <Link to={`${path}/:id`}><div className="card-img" 
                  onMouseEnter={() => setHovered(true)}
                  onMouseLeave={() => setHovered(false)}
                >                      
                <img src={images[imgIndex]}  className="img-responsive w-100" alt="" id="product-photo"></img>
                  {isHovered === true && 
                  <div className="d-flex card-img-overlay justify-content-between" id="arrows">
                      <button className="btn btn-light rounded-circle" id="left-arrow" onClick={(e) => { e.preventDefault(); imgIndex !== 0 && setImgIndex(imgIndex-1)}}><i className="fas fa-arrow-left"></i></button>
                      <button className="btn btn-light rounded-circle " id="right-arrow" onClick={(e) =>{ e.preventDefault(); imgIndex < images.length-1 && setImgIndex(imgIndex+1)}}><i className="fas fa-arrow-right"></i></button>
                  </div>}
              </div> </Link>                 

            
            <div className="card-body d-flex flex-column">
              <div className="d-flex mb-2">
                <h5 className="card-title" id="pris">{price + " kr"}</h5>
                <p className="card-text " id="pris">&middot;</p>
                <p className="card-text " id="pris">{location}</p>
                <div className="ms-auto">
                  <button className="btn btn-primary me-2" data-toggle="tooltip" title="Send Message" onClick={((e) => e.preventDefault())}><i className="fas fa-envelope"></i></button>
                  <button type="button" className="btn btn-outline-danger" data-toggle="tooltip" title="Add to Favorites" onClick={((e) => e.preventDefault())}><i className="fas fa-heart"></i></button>
                </div>
              </div>
                
              <p className="card-text me-2" style={{pointerEvents: 'none'}}>{title}</p>              
            </div>

            <Route path={`${match.path}/:moduleId`} render={routerProps => <Module {...routerProps} modules={modules} /> }/>
          </div>
          
      )   
}

export default Product;
