import './style.css';
import arrowLeft from './resources/arrow-left-circle.svg';
import arrowRight from './resources/arrow-right-circle.svg';
import logo from './logo.svg';
import iphone from './resources/iphonex.png';
import nature from './resources/nature.jpg';
import sport from './resources/sport.jpg';
import React from 'react';



class Product extends React.Component{
    constructor(props) {
        super(props);
    
        this.state = {
          isHovered: false,
        };
      }
    
      handleEnter() {
        this.setState({
          isHovered: true 
        });
      }
    
      handleLeave() {
        this.setState({
          isHovered: false 
        });
      }

    

      render(){

      
    return(
        <div class="card h-100 mb-4">
            <div class="card-img" onMouseEnter={this.handleEnter.bind(this)}
                          onMouseLeave={this.handleLeave.bind(this)} >
                <img src={this.props.img} class="card-img-top" alt="alt-image-cap" id="product-photo"/>  
                {this.state.isHovered && (
                    <div class="card-img-overlay justify-content-between mt-5" id="">
                    <button class="btn btn-light rounded-circle mt-4" id="left-arrow"><i class="fas fa-arrow-left"></i></button>
                    <button class="btn btn-light rounded-circle mt-4" id="right-arrow"><i class="fas fa-arrow-right"></i></button>
                </div>
                )}
            </div>

            <div class="card-body d-flex flex-column">
                <h5 class="card-title">6000 kr</h5>
                <p class="card-text"><small class="text-muted">Bergen</small></p>
                <p class="card-text">Pent brukt Iphone X. Kjopt in 2019. Ny pris: 2345 kr. Ma hentes.</p>
                <div class="text-center mt-auto">
                    <button class="btn btn-primary w-40 me-3">Send Message     <i class="far fa-envelope"></i></button>
                    <button type="button" class="btn btn-outline-danger w-40">Add to Favorites <i class="fas fa-heart"></i></button>
                </div>
            </div>
        </div>   
    );
                }
}

export default Product;
