import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { instanceAxs } from "../../api/Api";
import { useSelector } from "react-redux";

function ProductPage() {
    let {id} = useParams();

    const [annonce, setAnnonce] = useState();
    const [isLoading, setLoading] = useState(true)

    const findAnnonce = async () => {
        await instanceAxs.post('/getproduct', { productId: id})
            .then(respond => {
                console.log(respond);
                if(respond.data.message === "annonce found"){
                    setAnnonce(respond.data.annonce);
                    setLoading(false)
                } else {
                    console.log("something went wrong while getting the annonce")
                }
            })   
            .catch(err => {
                console.log(err);
            })   
    }

    useEffect(() => {
        findAnnonce();
    }, [isLoading])

    return(
        <div>
            { !isLoading ?
            <div>
                <p>product details</p>
                <h1>{annonce.title}</h1>
                <p>{id}</p>
                <p>{annonce.price}</p>
            </div>
            :
            <p>Loading...</p>
            }
        </div>
    )
}

export default ProductPage;
