{key: "statusUsed",value: "Used", filterKey: 'status', filterValue:'used'}, 
{key: "statusNew", value: "New", filterKey: 'status', filterValue: 'new'},
{key: "maxPrice", value: "til 1800", filterKey: 'price_max', filterValue: 1800}
]

addFilter(filterKey, filterValue, tagKey, tagValue){
  filter = {filterKey, filterValue}
  addTag(filterKey, filterValue, tagKey, tagValue)
}
removeFilter(filterKey, filterValue){
  var filterObject = filter.find(filterKey);
  if(filtersWithMultipleparams[filterObject] !== -1){
    remove(filterObject.value.filterValue)
    if(filterObject.value === ''){
      remove(filterObject)
    }
  } else {
    remove(filterObject)
  }
}
addTag(filterKey, filterValue, tagKey, tagValue){
  tag = {key: tagKey, value: tagValue, filterKey, filterValue}
}
removeTag(tagKey,filterKey, filterValue){
  tag.remove(tagKey)
  this.removeFilter(filterKey, filterValue)
}
*/
class SearchResult extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      items: [],
      filtersWithMultipleQueries: ["status", "location"],
      resultCount: 0,
      tagArray: [],
      urlParams: new URLSearchParams(props.location.search),
      filters: [
        {
          queryKey: "q",
          queryValue: new URLSearchParams(props.location.search).get("q"),
        },
        {
          key: "def",
          value: "asd",
        },
      ],
      minAndMaxPrice: undefined,
    };
  }

  setSortingCategory = (e) => {
    var sorting = e.target.value;
    this.setFilter("order", sorting, sorting);
  };

  getFiltersOnMount = () => {
    let urlParams = this.state.urlParams;

    var params = [];
    for (const [key, value] of urlParams) {
      var filter = {};

      switch(key){
        case 'q':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: undefined,
            tagValue: undefined,
          };
          params.push(filter)
          break;
        case 'price_max':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "maxPrice",
            tagValue: `til ${value}`,
          };
          params.push(filter);
          break;
        case 'price_min':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "minPrice",
            tagValue: `fra ${value}`,
          };
          params.push(filter);
          break;
        case 'status':
          filter = {
            queryKey: key,
            queryValue: value,
            tagKey: "status",
            tagValue: value,
          };
          params.push(filter);
          break;
      }
      
    }
    this.setState({
      filters: params,
    });
    this.getTags(params)
    return params;
  };

  getTags = (queryFilters) => {
    var filters = queryFilters === undefined ? this.state.filters : queryFilters;
    var tags = [];

    for (const filter of filters) {
      if (filter.tagKey !== undefined) {
        var tag = {
          key: filter.tagKey,
          value: filter.tagValue,
          queryKey: filter.queryKey,
          queryValue: filter.queryValue,
        };
        tags.push(tag);
      }
    }
    this.setState({
      tagArray: tags,
    });
    return tags;
  };

  makeSearch = () => {
    let filters = this.state.filters;
    var queryString = "";
    for (const filter of filters) {
      queryString += `${filter.queryKey}=${filter.queryValue}&`;
    }
    /* Object.entries(filters).forEach(
      ([key, value]) => (queryString += `${key}=${value}&`)
    ); */
    if (queryString[queryString.length - 1] === "&")
      queryString = queryString.slice(0, queryString.length - 1);
    let query = `/search?${queryString}`;
    instanceAxs
      .get(query)
      .then((response) => {
        // console.log("Search response", response);
        if (response.data.status) {
          var returnedItems = response.data.items;
          this.setState({
            items: returnedItems,
            resultCount: returnedItems.length,
          });
          window.history.pushState("page2", "seach made", query);
          // this.getFiltersOnMount();
          // window.location.href =  query
        } else {
          return console.log(response.data.message);
        }
      })
      .catch((err) => {
        console.log(err);
      });
  };

  setFilter = (queryKey, queryValue, tagKey, tagValue) => {
    var queryFilters = this.state.filters;
    var multipleQueryFilters = this.state.filtersWithMultipleQueries;
    var filter = queryFilters.find((element) => element.tagKey === tagKey);
    var index = queryFilters.indexOf(filter);

    if (queryValue === '') {
      if(index !== -1){
        queryFilters.splice(index,1)
        this.setState({
          filters: queryFilters
        });
        this.removeTag(tagKey, queryKey)
      }
      return;
    }

    if (index === -1) {
      var newFilter = {
        queryKey: queryKey,
        queryValue: queryValue,
        tagKey: tagKey,
        tagValue: tagValue,
      };

      queryFilters.push(newFilter);

      this.setState({
        filters: queryFilters,
      });
      this.addFilterTag(tagKey,tagValue,queryKey, queryValue);
      return;
    }

    if (multipleQueryFilters.includes(queryKey, 0)) {
      var qValue = filter.queryValue;
      if (qValue === undefined || qValue === null) qValue = "";

      if (!qValue.includes(queryValue)) qValue += " " + queryValue;
      qValue = qValue.trim();

      filter.queryValue = qValue;
      queryFilters[index] = filter;
      this.addFilterTag(tagKey, tagValue, filter, qValue)
    } else {
      filter.queryValue = queryValue;
      filter.tagValue = tagValue;
      queryFilters[index] = filter;
      this.addFilterTag(tagKey, tagValue, filter, queryValue)
      console.log("filters", queryFilters)
    }
    this.setState({
      filters: queryFilters,
    });
  };

    addFilterTag = (key, value, queryKey, queryValue) => {
    let tagArr = this.state.tagArray;
    var tagObj = {};
    var object = tagArr.find((obj) => obj.key === key);
    var objIndex = tagArr.indexOf(object);

    if (objIndex !== -1) {
      tagObj = {
        key,
        value: value,
        queryKey,
        queryValue: queryValue,
      };
      tagArr[objIndex] = tagObj;
    } else {
      tagArr.push({
        key: key,
        value: value,
        queryKey: queryKey,
        queryValue: queryValue,
      });
    }
    this.setState({
      tagArr: tagArr,
    });
  };

  removeFilter = (filter, queryValue) => {
    // remove a filter completely or erase a prop of it
    /* var queryFilters = this.state.filters;
    var multipleQueryFilters = this.state.filtersWithMultipleQueries;
    var qValue = queryValue || undefined;

    if(multipleQueryFilters.includes(filter)){
      if(queryFilters[filter] !== undefined && queryFilters[filter] !== null && qValue !== undefined){

        var qFilter = queryFilters[filter];
        var newQueryFilter = String(qFilter).replace(qValue, '');
        newQueryFilter = newQueryFilter.trim();
        queryFilters[filter] = newQueryFilter;
        if(queryFilters[filter] === '') {
          delete queryFilters[filter]; 
        } 
      }
    } else {
      if(queryFilters[filter] !== undefined && queryFilters[filter] !== null) {
        delete queryFilters[filter];
        // this.removeTag("",filter)
      }
    }
    this.setState({
