import { login, logout, matchStrings } from "../api/Api";

export const signIn = (user) => {
    return{
        type: "SIGN_IN",
        payload: user
    }
}

export const signOut = () => {
    return{
        type: "SIGN_OUT"
    }
}

export const signInMiddleware = store => next => action => {
    login(action.payload)
        .then(response => {
            if(response.data.message === "user logged in"){
                next(action);
            } else {
                alert("Could not login!")
            }
        })
}

export const signOutMiddleware = store => next => action => {
    logout()
        .then(response => {
            const msg = response.data.message;
            matchStrings(msg, "user logged out", alert("sa"));
        })
}
