import React from 'react';
import logo from './resources/nature.jpg';
import iphone from './resources/sport.jpg';
import arrowLeft from './resources/arrow-left-circle.svg';
import arrowRight from './resources/arrow-right-circle.svg';

import './App.css';



export default class ProductPage extends React.Component{

    constructor(props){
        super(props);
        this.state = {
            photos: [iphone,logo],
            indexPhoto: 0
        }
    }

    onclick(type){
        this.setState(prevState => {
           return {
               indexPhoto: type === 'next' ? (prevState.indexPhoto < prevState.photos.length-1 ? prevState.indexPhoto + 1: prevState.indexPhoto) : (prevState.indexPhoto !== 0 ? prevState.indexPhoto - 1: 0)}
        });
    }

    changeImg(img){
        this.setState({
            indexPhoto: this.state.photos.indexOf(img)
        })
    }

    render(){
        return(
            <div>
                <div id="frame">
                    <div class="border border-2 rounded-1" id="photo-frame">
                        <div>
                            <img src={arrowLeft} id="arrow-circle" onClick={this.onclick.bind(this, 'prev')} alt=""></img>
                            <img src={this.state.photos[this.state.indexPhoto]} id="photo" alt=""></img>
                            <img src={arrowRight} id="arrow-circle" onClick={this.onclick.bind(this, 'next')} alt=""></img>
                        </div>
                        <div>
                            <p>A very great mobile phone.</p>
                        </div>
                        <div>
                            <ul id="small-photo-div">
                                <li>
                                    <img src={iphone} id="small-photo" onClick={this.changeImg.bind(this,iphone)} alt=""></img>
                                </li>
                                <li>
                                    <img src={logo} id="small-photo" onClick={this.changeImg.bind(this,logo)} alt=""></img>
                                </li>
                            </ul>
                        </div>                        
                    </div>
                </div>
            </div>
        )
    }
}
