import axios from 'axios';

const config = require('../config/cfg.js');

var isLoggedIn = false;
var smplMsg = 'hello world'

const instanceAxs = axios.create({
    baseURL: config.serverApi,
    withCredentials: true
});

const checkLogin = () => {
    instanceAxs.get('/checklogin')
        .then(result => {
            console.log(result.data.message)
            return result.data.message;
        })
}

const setLogin = async () => {
    if ( checkLogin() === "user logged in"){
        isLoggedIn = true;
    } else {
        isLoggedIn = false;
    }
    console.log(checkLogin);
    console.log(isLoggedIn);
}

export {smplMsg, checkLogin, setLogin};
