import { useEffect, useState } from "react";
import { useParams } from "react-router";
import { instanceAxs } from "../../api/Api";

function ProductPage() {
    let {id} = useParams();

    const [users, setUsers] = useState([]);
    const [annonce, setAnnonce] = useState('');
    const [isLoading, setLoading] = useState(true)

    const findAnnonce = async () => {
        await instanceAxs.get('/users')
            .then(respond => {
                console.log(respond);
                setUsers(respond.data.users);
                const users2 = users.filter(checkIfHasAnnonce);
                console.log(users2)
                users2.map((user,index) => {
                    for(var i= 0; i < user.annonces.length; i++){
                        if(user.annonces[i] !== undefined || null){
                            if(user.annonces[i]._id === id){
                                
                                setAnnonce(user.annonces[i]);
                                return setLoading(false)
                            }
                        }

                    }
                })
            })
            .catch(err => {
                console.log(err)
            })

        
    }

    function checkIfHasAnnonce(user){
        return user.annonces !== undefined || null
    }

    function matchId(productId){

        return productId === id;
    }

    useEffect(() => {
        findAnnonce();
    }, [isLoading])

    return(
        <div>
            <p>product page</p>
            <p>{id}</p>
            {console.log(users)}
        </div>
    )
}

export default ProductPage;
