import React from "react";

import './CreateAnnonce.css';

class CreateAnnonce extends React.Component {

    constructor(props){
        super(props);
        this.state = {
            house : {
                name: "Enebolig",
                id: 4325,
                subs: [
                    {title : "kiralik", desc: "kiralik ev"},
                    {title : "satilik", desc: "satilik ev"},
                    {title : "komple", desc: "komple ev"},
                ]
            },
             car : {
                name: "Bil",
                id: 9876,
                subs: [
                    {title : "benzinli", desc: "benzinli araba"},
                    {title : "dizel", desc: "dizel araba"},
                ]
            },
             categories : [
                ...house, ...car
            ],
            currentCategory: '',
            currentSubs: []
        }
    }

    componentWillMount(){
        this.setState({
            currentCategory: house,
            currentSubs: house.subs
        })
    }



    render(){
        /*var house = {
