import React, { useState } from "react";
import Header from "./Header";
import "./Status.css";
import "./FilterComponents.css";

function Status(props) {
  const [isVisible, setVisible] = useState(true);
  const [statusNew, setStatusNew] = useState(false);
  const [statusUsed, setStatusUsed] = useState(false);
  const [checkedValues, setCheckedValues] = useState(props.checkedState)

  function toggleVisibality() {
    var visible = isVisible;
    setVisible(!visible);
  }

  function changeStatus(e,filter) {
    if(filter === "status_new") setStatusNew(!statusNew)     
    else if(filter === "status_used") setStatusUsed(!statusUsed);
  }
  React.useEffect(() => {
    console.log(props.checkedState)
  }, [props.checkedState])

  React.useEffect(() => {
    if(statusNew) props.setfilter("status","new","statusNew","Nytt");
    else props.removeFilter("status","new");
    // console.log("prop",props.checkedState)
  }, [statusNew])

  React.useEffect(() => {
      if(statusUsed) props.setfilter("status","used","statusUsed","Brukt")
      else props.removeFilter("status","used");
      // console.log("prop",props.checkedState)
  }, [statusUsed])

  React.useEffect(() => {
    var checkedState = String(props.checkedState)
    if(checkedState['new'] !== undefined){
      setStatusNew(true)
    } else {
      setStatusNew(false)
    }

    if(checkedState.includes('used')){
      setStatusUsed(true);
    } else {
      setStatusUsed(false)
    }

}, [props.checkedState])

  return (
    <div className="category border rounded priceStatusContainer filterContainer">
      <Header title="Status" toggleVisible={toggleVisibality} />
      <p>{props.checkedState}</p>
      {isVisible && (
        <div className="priceFilterComponents filterBody">
          <div className="form-check mb-2">
            <input
              className="form-check-input"
              type="checkbox"
              checked={statusNew}
              
              onChange={(e) => changeStatus(e,"status_new")}
            />
            <label
              className="form-check-label"
              style={{ cursor: "pointer" }}
              onClick={(e) => changeStatus(e,"status_new")}
            >
              Nytt (15/234)
            </label>
          </div>
          <div className="form-check">
            <input
              className="form-check-input"
              type="checkbox"
              checked={statusUsed}
              onChange={(e) => changeStatus(e,"status_used")}
            />
            <label
              className="form-check-label"
              style={{ cursor: "pointer" }}
              onClick={(e) => changeStatus(e,"status_used")}
            >
              Brukt (34)
            </label>
          </div>
        </div>
      )}
    </div>
  );
}

export default Status;
