import axios from "axios";
import { useEffect, useState } from "react";

function UploadImage(){

    const createImage = async (url) =>
        new Promise((resolve, reject) => {
            var image = new Image();
            image.addEventListener("load", () => resolve(image));
            image.addEventListener("error", (error) => reject(error));
            image.setAttribute("crossOrigin", "anonymous"); // needed to avoid cross-origin issues on CodeSandbox
            image.src = url;
        });

    const [doc, setDoc] = useState();
    const [image, setImage] = useState(null);


    const upload = async (e) => {
        const myImage = await createImage(image);
        const canvas = document.createElement("canvas");
        const ctx = canvas.getContext("2d");

        const maxSize = Math.max(image.width, image.height);
        const safeArea = 2 * ((maxSize / 2) * Math.sqrt(2));

        // set each dimensions to double largest dimension to allow for a safe area for the
        // image to rotate in without being clipped by canvas context
        canvas.width = safeArea;
        canvas.height = safeArea;

        // translate canvas context to a central location on image to allow rotating around the center.
        ctx.translate(safeArea / 2, safeArea / 2);
        ctx.rotate(getRadianAngle(rotation));
        ctx.translate(-safeArea / 2, -safeArea / 2);

        // draw rotated image and store data.
        ctx.drawImage(
            image,
            safeArea / 2 - image.width * 0.5,
            safeArea / 2 - image.height * 0.5
        );

        const data = ctx.getImageData(0, 0, safeArea, safeArea);

        // set canvas width to final desired crop size - this will clear existing context
        canvas.width = pixelCrop.width;
        canvas.height = pixelCrop.height;

        // paste generated rotate image with correct offsets for x,y crop values.
        ctx.putImageData(
            data,
            0 - safeArea / 2 + image.width * 0.5 - pixelCrop.x,
            0 - safeArea / 2 + image.height * 0.5 - pixelCrop.y
        );

        // As Base64 string
        // return canvas.toDataURL("image/jpeg");
        return canvas;
    }

    const getImage = () => {
        axios.get("http://localhost:3080/file?type=img&id=61a45dee1d898f49a3b1f2da")
            .then()
    }

    const onFileChange = event => {
        if(event.target.files && event.target.files.length > 0){
            const reader = new FileReader();
            reader.readAsDataURL(event.target.files[0]);
            reader.addEventListener('load', () => {
                console.log(reader.result);
                setImage(reader.result);
            })
        }
    };


    return(
        <div>
            <div className="upload">
                <input className="form-control" accept="image/*" type="file" id="file" name="file"  onChange={onFileChange} style={{marginBottom: '10px'}}></input>
                <button type="button" className="btn btn-primary" onClick={upload}>Upload</button>
            </div>
            <hr/>
            <div className="show-images">
                <div>
                    <img src="" width="250" height="250" alt="pic"/>
                </div>
                <button type="button" className="btn btn-primary mt-3" onClick={getImage}>Show image</button>
            </div>
        </div>
    )
}

export default UploadImage;

