import React from 'react';
import ProductCard from '../../Component/ProductCard/ProductCard.js';
import data from '../../data/products.json';

import { instanceAxs } from '../../api/Api.js';
import { fileApi } from '../../config/cfg.js';

class Menu extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            items: '',
            isLoading: true
        }
    }

     async getItems () {
        await instanceAxs.get(`${fileApi}/getmenuitems`)
        .then(response => {
            console.log(response);
            this.setState({
                items: response.data.items
            })
        })
        console.log("items", this.state.items)
    }
    componentDidMount(){
        this.getItems();
    }
    
    render(){
        return(
            <div className="container" id="menu-div">
                <div className="row">  
                    { (this.state.items.length > 0 && this.state.isLoading === false) ? 
                    
                    (this.state.items.map(data => {
                        return(
                            <div className="col-md-4 mb-4" key={data.name}>
                                <ProductCard 
                                    img={data.images} 
                                    price={data.price} 
                                    name={data.name} 
                                    location={data.location} 
                                    id={data.id}
                                    data={data}/>
                            </div>
                        ) 
                    }))
                    : 
                    <p>loading</p>
                }                                                      
                </div>
            </div>       
        )
    }   
}

export default Menu;
