import axios from 'axios';

const config = require('../config/cfg.js');

var isLoggedIn = false;

const instanceAxs = axios.create({
    baseURL: config.serverApi,
    withCredentials: true
});

const checkLogin = async () => {
    instanceAxs.get('/checklogin')
        .then(result => {
            let response = result.data.message;
            console.log(result);

            if (response === "user logged in"){
                isLoggedIn = true;
            } else {
                isLoggedIn = false;
            }
            console.log(isLoggedIn);
            return result.data.message;
        })
}

export {isLoggedIn, checkLogin};
