import React, { useEffect, useState } from 'react';
import './style.css';

function Product (props){
  const photo = ["https://static.toiimg.com/photo/msid-58515713,width-96,height-65.cms","https://i.ytimg.com/vi/p7TDpx0hsn4/maxresdefault.jpg"];
  const [imgs,title,location,price,desc] = [props.img, props.name,props.location,props.price,props.desc];

  var [isHovered, setHovered] = useState(true);
  const [imgIndex, setImgIndex] = useState(0);

  function name(params) {
    setHovered(true)
  }

      return(
          <div className="card h-100 border-2 shadow">
              <div className="card-img" 
                 onMouseEnter={() => setHovered(true)}
                  onMouseLeave={() => setHovered(false)}
                >             
                <img src={photo[imgIndex]}  className="img-responsive w-100" alt="alt-image-cap" id="product-photo"/>
                  {isHovered == true && 
                  <div className="d-flex card-img-overlay justify-content-between" id="arrows">
                      <button className="btn btn-light rounded-circle" id="left-arrow" onClick={setImgIndex(imgIndex-1)}><i className="fas fa-arrow-left"></i></button>
                      <button className="btn btn-light rounded-circle " id="right-arrow" onClick={setImgIndex(imgIndex+1)}><i className="fas fa-arrow-right"></i></button>
                  </div>}
              </div>
            
            <div className="card-body d-flex flex-column">
              <div className="d-flex mb-2">
                <h5 className="card-title" id="pris">{price + " kr"}</h5>
                <p className="card-text " id="pris">&middot;</p>
                <p className="card-text " id="pris">{location}</p>
                <div className="ms-auto">
                  <button className="btn btn-primary me-2" data-toggle="tooltip" title="Send Message"><i className="fas fa-envelope"></i></button>
                  <button type="button" className="btn btn-outline-danger" data-toggle="tooltip" title="Add to Favorites"><i className="fas fa-heart"></i></button>
                </div>
              </div>
                
              <p className="card-text me-2">{title}</p>              
            </div>
          </div>              
      )   
}

export default Product;
