import React, { Component } from 'react';
import './App.css';
import { getAllUsers, createUser } from './service.js';

class App extends Component {

  state = {
    user: {},
    users: [],
    numberOfUsers: 0
  }

  createUser = (e) => {
    let user = {
        name: "ahme"
    }
      createUser(user)
        .then(response => {
          console.log(response);
          this.setState({numberOfUsers: this.state.numberOfUsers + 1})
      });
  }

  getAllUsers = () => {
    getAllUsers()
      .then(users => {
        console.log(users)
        this.setState({users: users, numberOfUsers: users.data.length})
      });
  }

  onChangeForm = (e) => {
      let user = this.state.user
      if (e.target.name === 'firstname') {
          user.firstName = e.target.value;
      } else if (e.target.name === 'lastname') {
          user.lastName = e.target.value;
      } else if (e.target.name === 'email') {
          user.email = e.target.value;
      }
      this.setState({user})
  }

  render() {

    return (
        <div>

            {this.state.numberOfUsers}
            <ul>
              {Object.entries(this.state.users).map(([key,value]) => {
                return(
                  console.log(value)
                )
              })}
            </ul>
            <button type="button" onClick={() => this.getAllUsers()}>show</button>
            <button type="button" onClick={() => this.createUser()}>create</button>
            </div>

    );
  }
}

export default App;
