import { persistReducer } from 'redux-persist'
import localStorage from 'redux-persist/es/storage';
import sessionStorage from 'redux-persist/es/storage/session';
import { combineReducers } from 'redux';
// import { rootReducer } from '../reducers/rootReducer'
import LoginReducer from '../reducers/LoginReducer';
import userReducer from '../reducers/userReducer';
import SearchReducer from '../reducers/SearchReducer';

const persistConfig = {
  //timeout: 2000,
  key: 'root',
  storage: localStorage,
  blacklist: ['filters']
}

export const sessionConfig = {
  key: 'user',
  storage: sessionStorage
}

const rootReducer = combineReducers({ 
  isLogged: LoginReducer,
  user: userReducer,
  filters: persistReducer(sessionConfig, SearchReducer)
}); 

export const loadSessionState = () => {
  try {
    const serializedSessionState = window.sessionStorage.getItem('sessionData');
    const serializedLocalState = window.localStorage.getItem('state');

    if (serializedSessionState === null || serializedLocalState === null) {
      return undefined;
    } 
    const sessisonData = JSON.parse(serializedSessionState);
    sessisonData = {"type": "session",...sessisonData}; 
    const localData = JSON.parse(serializedLocalState);
    localData = {"type": "local",...localData}; 
    let finalData =" {...sessisonData, ...localData}"
    console.log("finalData", finalData)
    return finalData
  } catch(err) {
    console.log(err);
    return undefined;
  }
}

export const saveSessionState = (sessionState) => {
  try {
    const serializedSessionState = JSON.stringify(sessionState);
    window.sessionStorage.setItem("sessionData", serializedSessionState);
  } catch (err) {
    console.log(err);
  }
}

const loadState = () => {
    try {
      const serializedState = window.localStorage.getItem('state');

      if(serializedState === null) {
        return undefined;
      }
      return JSON.parse(serializedState);
    } catch (err) {
      console.log(err);
      return undefined;
    }
};

export const saveState = (state) => {
    try {
      const serializedState = JSON.stringify(state);

      window.localStorage.setItem('state', serializedState);
    } catch (err) {
      console.log(err);
    }
};

export const persistedState = loadSessionState();
export const persistedReducer = persistReducer(persistConfig, rootReducer)
