import { useSelector } from "react-redux";
import { login } from "../api/Api";
import { instanceAxs } from "../api/Api";

var initialState = {
    logged: false
}
const isLoggedReducer =  (state = initialState, action) => {
    switch (action.type) {
        case "SIGN_IN":
            return { ...state, logged: state.value + 1 };
        case "SIGN_OUT":
            return state.isLogged = true
        default:
            return state;
    }
}

export function LoggedSelector(){
    return useSelector(state => state.isLogged);
}

export default isLoggedReducer;
