import React, { useState } from 'react';
//import data from './data/products.json';
import './App.css';
import './ProductMenu.css';
import data from './data/products.json';
import ProductCard from './ProductCard';
import Slider from '@material-ui/core/Slider';


const ProductMenu = ({ match }) => {

    const [renderCategPrice, setRengerCategPrice] = useState(true);
    const [priceValue, setPriceValue] = useState([0, 100]);

    const setCategoryVisibility = (category) => {
        if(category === "renderCategPrice"){
            setRengerCategPrice(!renderCategPrice);
        }
    }

    const handlePriceSliderChange = (event, newValue) => {
        setPriceValue(newValue);
    };

    const handle = (event, value) => {
        setPriceValue([event.target.value,priceValue[1]]);
    }
 
    return(
        <div className="container main-con">
            <div className="product-categories">
                <p>Eindom</p>
                <div class="input-group mb-3">
                    <input type="text" className="form-control" placeholder="Søk i eindom..."></input>
                    <button class="btn btn-outline-secondary" type="button"><i class="fas fa-search"></i></button>                    
                </div>
                <button className="btn btn-info w-100 mb-2">Lagre Søk</button>
                <button className="btn btn-primary w-100 mb-3">Bruk Endringer</button>
                <div className="category border rounded">
                    <div id="category-header" onClick={() => setCategoryVisibility("renderCategPrice")}>
                            <span>Pris</span>
                            <i class="fas fa-chevron-down"></i>
                    </div>
                    <div className="category-content">
                        {renderCategPrice &&
                            <div class="pricecontainer" style={{marginTop: "25px",marginBottom: "15px"}}>
                                <div className="price-slider w-100">
                                    <div id="priceOverSlider">
                                        <label className="text-muted"><small>{priceValue[0]} kr</small></label>
                                        <label className="text-muted"><small>{priceValue[1]} kr</small></label>
                                    </div>
                                    <Slider
                                        value={priceValue}
                                        onChange={handlePriceSliderChange}
                                        valueLabelDisplay="auto"
                                        aria-labelledby="range-slider"
                                        max="100"
                                    />
                                </div>
                                <div className="price-inputs form-group">
                                    <input type="number" className="form-control" id="min-price-input" placeholder="min. pris" value={priceValue[0]} onChange={handle}></input>
                                    <input type="number" className="form-control" id="max-price-input" placeholder="max. pris"></input>
                                </div>
                                <button className="btn btn-primary w-100 mt-3">Søk</button>
                            </div>
                        }
                    </div>
                </div>
                <div className="category border rounded">
                    <div id="category-header">
                            <span>Pris</span>
                            <i class="fas fa-chevron-down"></i>
                    </div>
                    <div className="category-content">
                        
                    </div>
                </div>
                
            </div>
            <div className="products-div">
                <div className="sort-dropdown-menu">
                    <span>34 Treff</span>
                    <select class="form-select" id="sortby-menu" style={{width: "200px"}}>
                        <option selected value="publishedNew">Nyeste først</option>
                        <option value="publishedOld">Eldste først</option>
                        <option value="priceLowHigh">Pris lav-høy</option>
                        <option value="priceHighLow">Pris høy-lav</option>
                    </select>           
                </div>
                <div className="tags">
                    <span className="border rounded-pill">Mazda</span>
                    <span className="border rounded-pill">Til Salgs</span>
                </div>
                <div className="products">
                    <div class="row">  
                        {data.map(data => {
                            return(
                                <div class="col-md-4 mb-4" key={data.name}>
                                    <ProductCard 
                                        img={data.images} 
                                        price={data.price} 
                                        name={data.name} 
                                        location={data.location} 
                                        id={data.id}
                                        data={data}/>
                                </div>
                            ) 
                        })}                                                      
                    </div>
                </div>  
            </div>
        </div>
    )
}

export default ProductMenu;
